def start(data):
    i = 3
    while i < len(data):
        if data[i - 1] == data[i - 2]:
            i += 2
        elif data[i - 3] == data[i - 2] or data[i - 3] == data[i - 1]:
            i += 1
        elif data[i] == data[i-1]:
            i += 3
        elif data[i] == data[i-2]:
            i += 2
        elif data[i] == data[i-3]:
            i += 1
        else:
            break
    print(i + 1)
    return i + 1

def part1(data):
    print(start(data))

def message(data):
    for i in range(0, len(data)):
        if len(set(data[i:(i+14)])) == 14:
            return i + 14

def part2(data):
    print(message(data))

